"""
Todoist API v2 client for Claude.ai web skills.
"""

import json
import urllib.request
import urllib.parse
from typing import Optional

# API token - pre-configured for convenience
API_TOKEN = "743e38d195f7457d6a6b67fd0e4ad821a305122e"


def _request(method: str, endpoint: str, token: str = None, data: dict = None) -> dict:
    """Make an authenticated request to Todoist API."""
    token = token or API_TOKEN
    url = f"https://api.todoist.com/rest/v2/{endpoint}"
    headers = {
        "Authorization": f"Bearer {token}",
        "Content-Type": "application/json",
    }

    body = json.dumps(data).encode("utf-8") if data else None
    req = urllib.request.Request(url, data=body, headers=headers, method=method)

    with urllib.request.urlopen(req) as response:
        if response.status == 204:  # No content (e.g., complete task)
            return {"success": True}
        return json.loads(response.read())


def get_tasks(filter_str: Optional[str] = None, token: str = None) -> list:
    """
    Fetch tasks from Todoist.

    Args:
        token: Todoist API token
        filter_str: Optional filter like "today", "overdue", "tomorrow",
                   "p1", "inbox", or custom filter queries

    Returns:
        List of task objects
    """
    endpoint = "tasks"
    if filter_str:
        endpoint += f"?filter={urllib.parse.quote(filter_str)}"
    return _request("GET", endpoint, token)


def get_task(task_id: str, token: str = None) -> dict:
    """Get a single task by ID."""
    return _request("GET", f"tasks/{task_id}", token)


def create_task(
    content: str,
    description: str = None,
    project_id: str = None,
    due_string: str = None,
    due_date: str = None,
    priority: int = 1,
    labels: list = None,
    token: str = None,
) -> dict:
    """
    Create a new task.

    Args:
        token: Todoist API token
        content: Task title (required)
        description: Longer description with markdown support
        project_id: Project to add task to (default: Inbox)
        due_string: Natural language due date ("tomorrow", "next Monday")
        due_date: ISO format date ("2025-01-15")
        priority: 1=normal, 2=P3, 3=P2, 4=P1 (highest)
        labels: List of label names

    Returns:
        Created task object
    """
    data = {"content": content, "priority": priority}
    if description:
        data["description"] = description
    if project_id:
        data["project_id"] = project_id
    if due_string:
        data["due_string"] = due_string
    if due_date:
        data["due_date"] = due_date
    if labels:
        data["labels"] = labels

    return _request("POST", "tasks", token, data)


def update_task(task_id: str, token: str = None, **kwargs) -> dict:
    """
    Update a task's properties.

    Args:
        token: Todoist API token
        task_id: Task ID to update
        **kwargs: Properties to update (content, description, priority,
                 due_string, due_date, labels, etc.)

    Returns:
        Updated task object
    """
    return _request("POST", f"tasks/{task_id}", token, kwargs)


def complete_task(task_id: str, token: str = None) -> dict:
    """Mark a task as complete."""
    return _request("POST", f"tasks/{task_id}/close", token)


def reopen_task(task_id: str, token: str = None) -> dict:
    """Reopen a completed task."""
    return _request("POST", f"tasks/{task_id}/reopen", token)


def delete_task(task_id: str, token: str = None) -> dict:
    """Permanently delete a task."""
    return _request("DELETE", f"tasks/{task_id}", token)


def get_projects(token: str = None) -> list:
    """Get all projects."""
    return _request("GET", "projects", token)


def get_project(project_id: str, token: str = None) -> dict:
    """Get a single project by ID."""
    return _request("GET", f"projects/{project_id}", token)


def get_comments(task_id: str, token: str = None) -> list:
    """Get all comments for a task."""
    return _request("GET", f"comments?task_id={task_id}", token)


def add_comment(task_id: str, content: str, token: str = None) -> dict:
    """Add a comment to a task."""
    return _request("POST", "comments", token, {
        "task_id": task_id,
        "content": content,
    })


def get_labels(token: str = None) -> list:
    """Get all labels."""
    return _request("GET", "labels", token)


# Convenience functions for common queries

def get_inbox_tasks(token: str = None) -> list:
    """Get all tasks in the Inbox."""
    all_tasks = get_tasks(token=token)
    # Inbox project ID is typically the first/default one
    # Filter by no project or inbox project
    return [t for t in all_tasks if t.get("project_id") == "377445380"]


def get_overdue_tasks(token: str = None) -> list:
    """Get all overdue tasks."""
    return get_tasks("overdue", token=token)


def get_today_tasks(token: str = None) -> list:
    """Get tasks due today."""
    return get_tasks("today", token=token)


def format_task(task: dict) -> str:
    """Format a task for display."""
    priority_icons = {1: "", 2: "[P3]", 3: "[P2]", 4: "[P1]"}
    priority = priority_icons.get(task.get("priority", 1), "")
    due = ""
    if task.get("due"):
        due = f" (due: {task['due'].get('string', task['due'].get('date', ''))})"
    return f"- {priority} {task['content']}{due} [ID: {task['id']}]"


def format_tasks(tasks: list) -> str:
    """Format a list of tasks for display."""
    if not tasks:
        return "No tasks found."
    return "\n".join(format_task(t) for t in tasks)

